package net.abc.yyy.controller;import java.util.Map;import javax.annotation.Resource;import javax.servlet.http.HttpSession;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import net.abc.yyy.mapper.UserMapper;/** *  * @author huangxin <3203317@qq.com> * */@RequestMapping(value = "/user")@Controllerpublic class UserController extends BaseController {	@Resource	UserMapper userMapper;	/**	 * 	 * @param session	 * @param map	 * @return	 */	@RequestMapping(value = { "/login" }, method = RequestMethod.GET)	public String login(HttpSession session, Map<String, Object> map) {		map.put("verify_token", genVerifyToken(session));		return "user/login";	}}