package net.abc.xxx.model;import java.io.Serializable;import java.util.Date;import javax.persistence.Column;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.Table;/** * * @author huangxin <3203317@qq.com> * */@Table(name = "w_notice")public class Notice implements Serializable {	private static final long serialVersionUID = 4204799309802253673L;	@Id	@Column(name = "id")	@GeneratedValue(generator = "UUID")	private String id;	private String title;	private String content;	private Date create_time;	private String create_user_id;	/**	 * 查看数	 */	private Integer view_count;	/*-----分割线-----*/	public String getId() {		return id;	}	public Integer getView_count() {		return view_count;	}	public void setView_count(Integer view_count) {		this.view_count = view_count;	}	public void setId(String id) {		this.id = id;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public Date getCreate_time() {		return create_time;	}	public void setCreate_time(Date create_time) {		this.create_time = create_time;	}	public String getCreate_user_id() {		return create_user_id;	}	public void setCreate_user_id(String create_user_id) {		this.create_user_id = create_user_id;	}}